// client/src/App.js
import React from "react";
import { useEffect } from "react";
import axios from "axios";
import { useDispatch } from "react-redux";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import Navbar from "./components/Navbar";
import "./index.css";
import Login from "./pages/Login";
import Signup from "./pages/Signup";
import Home from "./pages/Home"; // דף ברירת מחדל
import Files from "./pages/Files"; // אם יש לך דף כזה
import { login } from "./redux/authSlice";
function App() {
  const dispatch = useDispatch();
  useEffect(() => {
    const token = document.cookie
      .split("; ")
      .find((row) => row.startsWith("authToken="));

    console.log("token", token);

    if (token) {
      // שליחה לשרת לוודא שה-token תקין
      axios
        .get("http://localhost:5001/api/auth/verifyToken")
        .then((response) => {
          if (response.data.user) {
            dispatch(login(response.data.user)); // עדכון ה-state אם ה-token תקין
          }
        })
        .catch((err) => console.error("Token verification failed:", err));
    }
  }, [dispatch]);

  return (
    <Router>
      <Navbar />
      <div className="min-h-screen bg-gray-100">
        <main className="container mx-auto px-4 py-8">
          <Routes>
            <Route path="/login" element={<Login />} />
            <Route path="/signup" element={<Signup />} />
            <Route path="/files" element={<Files />} /> {/* אם יש לך דף כזה */}
            <Route path="/" element={<Home />} /> {/* דף ברירת מחדל */}
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
